// ============================================================
// Helper functions
// ============================================================

export function getPlanetInSignDescription(planet: string, sign: string): string {
  return PLANET_IN_SIGN_DESCRIPTIONS[`${planet}_${sign}`] || "";
}

export function getPlanetInHouseDescription(planet: string, house: number): string {
  return PLANET_IN_HOUSE_DESCRIPTIONS[`${planet}_${house}`] || "";
}

export function getAscendantDescription(sign: string): string {
  return ASCENDANT_DESCRIPTIONS[sign] || "";
}

export function getMidheavenDescription(sign: string): string {
  return MIDHEAVEN_DESCRIPTIONS[sign] || "";
}

export function getAspectDescription(aspectKey: string): string {
  return ASPECT_DESCRIPTIONS[aspectKey] || "";
}

export function getElementDescription(element: string): string {
  return ELEMENT_DESCRIPTIONS[element] || "";
}

export function getModalityDescription(modality: string): string {
  return MODALITY_DESCRIPTIONS[modality] || "";
}
